mapper.select:
SELECT
FORMAT(
(SELECT COUNT(terminalType) FROM t_mdn_monitor
WHERE 1 = 1

AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  >=  #{start}


AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  <=  #{end}

) /
(SELECT COUNT(terminalType) FROM t_mdn_monitor)
,2) AS ratio
FROM DUAL

SELECT CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER) AS DATE,
COUNT(terminalType)  AS qty FROM t_mdn_monitor
WHERE 1 = 1

AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  >=  #{start}


AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  <=  #{end}

GROUP BY DATE

SELECT
FORMAT(
(SELECT COUNT(mdn) FROM t_mdn_monitor
WHERE 1 = 1

    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  >=  #{start}


    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  <=  #{end}

) /
(SELECT COUNT(mdn) FROM t_mdn_monitor)
,2) AS ratio
FROM DUAL

SELECT CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER) AS DATE,
COUNT(mdn)  AS qty FROM t_mdn_monitor
WHERE 1 = 1

    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  >=  #{start}


    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  <=  #{end}

GROUP BY DATE

SELECT
sourceArea,
COUNT(sourceArea) AS qty
FROM
t_mdn_monitor
WHERE 1 = 1

    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  >=  #{start}


    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  <=  #{end}

GROUP BY sourceArea
ORDER BY qty DESC;
