mapper.select:
SELECT  COUNT(DISTINCT fraudedMdn) AS qty
FROM t_user_affected
WHERE 1=1

    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  >=  #{start}


    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)   <=  #{end}

SELECT sourceId, COUNT(sourceId) AS qty FROM t_user_affected WHERE 1 = 1
AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER) >= #{start}
AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER) <= #{end}
GROUP BY sourceId ORDER BY qtys DESC

SELECT type,COUNT(*) AS qty FROM t_user_affected WHERE 1 = 1
AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER) >= #{start}
AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER) <= #{end}
group by type

SELECT mdn,COUNT(*) AS qty
FROM t_user_affected
WHERE 1 = 1

    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  >=  #{start}


    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)   <=  #{end}

GROUP BY mdn
ORDER BY qty DESC
LIMIT 5;

SELECT TIME,
COUNT(fraudedMdn) AS qty
FROM t_user_affected
WHERE 1 = 1

    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  >=  #{start}


    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)   <=  #{end}

GROUP BY TIME;
