mapper.select:
SELECT mdn,SUM(duration) AS qty
FROM t_user_affected_replay
WHERE 1=1
AND TYPE=1 OR TYPE=2

    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  >=  #{start}


    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)   <=  #{end}

GROUP BY mdn
ORDER BY qty DESC
LIMIT 5;

SELECT TYPE,COUNT(*) AS qty
FROM t_user_affected_replay
WHERE 1=1
AND mdn = #{mdn}

    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)  >=  #{start}


    AND CAST(DATE_FORMAT(`time`, '%Y%m%d') AS SIGNED INTEGER)   <=  #{end}

GROUP BY TYPE
